import React, {useState, useEffect} from 'react';
import axios from 'axios'

export default function Dnatools() {

  const [dnaresult, setDnaresult] = useState('')

  const getReverseComp = () => {
    const apiUrl = `http://127.0.0.1:8000/api/dna/utils/reverse_complement/`;
    axios.get(apiUrl, {'seq': seq })
      .then(res => {
        console.log(res)
        setDnaresult(res.data.content)
      }).catch(err => {
        console.log(err)
      })
  };

  const getNucFreq = () => {
    axios.post('http://localhost:8000/api/dna/utils/nucleotide_freq/', {'seq': seq })
      .then(res => console.log(res))
  };

  const getCgContent = () => {
    axios.post('http://localhost:8000/api/dna/utils/cg_freq/', {'seq': seq })
      .then(res => console.log(res))
  };

  const toAminoAcid = () => {
    axios.post('http://localhost:8000/api/dna/utils/translate_to_aminoacids/', {'seq': seq })
      .then(res => console.log(res))
  };

  const getFrames = () => {
    axios.post('http://localhost:8000/api/dna/utils/frames/', {'seq': seq })
      .then(res => console.log(res))
  };

  return <div>
        <div className='App list-group-item justify-content-center align-items center mx-auto' style={{"width":"800px", "backgroundColor":"white", "marginTop":"15px"}}>
          <h4 className='card text-white bg-info mb-1' styleName="max-width: 20rem">
            DNA Tools
            </h4>
            <div className='card-body'>
              <span className="card-text">
                <textarea class="mb-2 form-control seqIn" id="seqTextarea" rows="3" placeholder="Paste your sequence here"></textarea>
                <button className="btn btn-outline-primary mx-2 mb-3" 
                style={{'borderRadius':'50px',"font-weight":"bold"}} onClick={getReverseComp}>
                  Reversal Complement</button>
                <button className="btn btn-outline-primary mx-2 mb-3" 
                style={{'borderRadius':'50px',"font-weight":"bold"}} onClick={getNucFreq}>
                  Nucleotide Frequence</button>
                <button className="btn btn-outline-primary mx-2 mb-3" 
                style={{'borderRadius':'50px',"font-weight":"bold"}} onClick={getCgContent}>
                  CG Content</button>
                <button className="btn btn-outline-primary mx-2 mb-3" 
                style={{'borderRadius':'50px',"font-weight":"bold"}} onClick={toAminoAcid}>
                  To Amino Acid</button>
                <button className="btn btn-outline-primary mx-2 mb-3" 
                style={{'borderRadius':'50px',"font-weight":"bolgetFramesd"}} onClick={getFrames}>
                  Frames</button>
              </span>
              <h5 className="card text-white bg-success mb-3">Result</h5>
              <div >
                { dnaresult && <p>{dnaresult}</p> }
              </div>
            </div>
        </div>
</div>
}
